import React, { useState } from 'react';

// Pictures
import cardLogo from '../../images/card-logo.svg';

function Recto() {
    const [cardNumber, setCardNumber] = useState('0000 0000 0000 0000');
    const [cardName, setCardName] = useState('Jane appleassed');
    const [expMonth, setExpMonth] = useState('00');
    const [expYear, setExpYear] = useState('00');

  return (
    <div className='bgRecto w-[55vh] h-[30vh] rounded-lg -ml-[90%]'>
        <div className='h-[50%] p-[5%] '>
            <img src={cardLogo} alt="" />
        </div>
        <div className='h-[50%] w-full px-[10%] flex flex-col justify-center items-start gap-5'>
            <div>
                <span className='text-xl text-white tracking-widest font-bold'>{cardNumber}</span>
            </div>
            <div className='w-full flex justify-between'>
                <div>
                    <span>{cardName}</span>
                </div>
                <div>
                    <span>{expMonth}/{expYear}</span>
                </div>
            </div>
        </div>
    </div>
  )
}
export default Recto;
