import React, { useState } from 'react';

// Components 
import Error from './Error';

function ExpDateInput({setExpMonth, setExpYear}) {
  const [error, setError] = useState(false);
  const [message, setMessage] = useState('');
  const [mm, setMM] = useState('');
  const [yy, setYY] = useState('');

  const handleExpMonthInput = ({target}) => {
    const input = target.value;
    const v = input.slice(0,2);
    if(!v){
      setMessage('Can\'t be blank');
      setError(true);
    }else if(target.value < 1 || target.value > 12){
      setMessage('Logic error');
      setError(true);
    }else {
      setError(false);
    };
    setMM(v);
    setExpMonth(v? v : '00');
  }

  const handleExpYearInput = ({target}) => {
    const input = target.value;
    const v = input.slice(0,2);
    let date = new Date();
    if(!v){
      setMessage('Can\'t be blank');
      setError(true);
    }else if((target.value < date.getYear()) || (target.value > (date.getYear() + 10))){
      setMessage('Logic error');
      setError(true);
    }else {
      setError(false);
    };
    setYY(v);
    setExpYear(v? v : '00');
  }

  return (
    <div className='w-[55%] flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">Exp. date (MM/YY)</label>
      <div className='w-full flex justify-between gap-1'>
        <input value={mm} onChange={handleExpMonthInput} className={`numberInput w-[50%] ${error ? 'border-red-500' : 'border-slate-200'} border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg `} type="number" inputMode='numeric' placeholder='MM' required />
        <input value={yy} onChange={handleExpYearInput} className={`numberInput w-[50%] ${error ? 'border-red-500' : 'border-slate-200'} border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg `} type="number" inputMode='numeric' placeholder='YY' required />
      </div>
      {
        error && (
          <Error message={message} />
        )
      }
    </div>
  )
}
export default ExpDateInput;
