import React, { useState } from 'react';

// Components
import Error from './Error';

function CardNumberInput({setCardNumber}) {
  const [inputValue, setInputValue] = useState('');
  const [error, setError] = useState(false);
  const [message, setMessage] = useState('');

  const handleNumberInput = ({ target }) => {
    const inputNumber = target.value.replace(/ /g, '');
    const formattedNumber = inputNumber.match(/.{1,4}/g)?.join(' ');
    const pattern = /[a-zA-Z]/;
    if(!formattedNumber){
      setMessage('Can\'t be blank');
      setError(true);
    }else if(pattern.test(formattedNumber)){
      setMessage('Wrong format, numbers only');
      setError(true);
    }else if(formattedNumber.length < 16){
      setMessage('Card number not valid');
      setError(true);
    }else{
      setError(false);
    }
    setInputValue(formattedNumber || '');
    setCardNumber(formattedNumber || '0000 0000 0000 0000');
  };

  return (
    <div className='w-full flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">Card Number</label>
      <input value={inputValue} onChange={handleNumberInput} className={`${error ? 'border-red-500' : 'border-slate-200'} border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg `} type="text" inputMode='numeric' maxLength='19' placeholder='e.g. 1234 5678 9123 0000'  />
      {
        error && (
          <Error message={message} />
        )
      }
    </div>
  )
}
export default CardNumberInput;
