import React from 'react';

function PlaceHolderName({setCardName}) {

  const handleNameInput = ({target}) => {
    setCardName(target.value);
  }

  return (
    <div className='w-full flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">Card Holder Name</label>
      <input onChange={handleNameInput} className='border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg' type="text" placeholder='e.g. Jane Applessed' />
    </div>
  )
}
export default PlaceHolderName;
