import React from 'react';

function CardNumberInput({setCardNumber}) {

  const handleNumberInput = ({ target }) => {
    // Remove leading and trailing spaces from the input
    const inputValue = target.value.trim();
  
    // Use a regex to extract only numeric characters from the input
    const numericInput = inputValue.replace(/\D/g, '');
  
    // Split the numeric input into chunks of 4 characters and join with spaces
    const formattedInput = numericInput.match(/.{1,4}/g).join(' ');
  
    // Update the state with the formatted value
    setCardNumber(formattedInput);
  };

  return (
    <div className='w-full flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">Card Number</label>
      <input onChange={handleNumberInput} className='border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg' type="text" placeholder='e.g. 1234 5678 9123 0000' />
    </div>
  )
}
export default CardNumberInput;
