import React from 'react';

function CvcInput({setCvc}) {

  const handleCvcInput = ({target}) => {
    setCvc(target.value? target.value : '000');
  }

  return (
    <div className='w-[40%] flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">cvc</label>
      <input onChange={handleCvcInput} className='numberInput border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg' type="text" maxLength='3' inputMode='numeric' placeholder='e.g. 123' />
    </div>
  )
}
export default CvcInput;
