import React, { useState } from 'react'

function ExpDateInput({setExpMonth, setExpYear}) {
  const [mm, setMM] = useState('');
  const [yy, setYY] = useState('');

  const handleExpMonthInput = ({target}) => {
    const input = target.value;
    const v = input.slice(0,3);
    setMM(v);
    setExpMonth(v? v : '00');
  }

  const handleExpYearInput = ({target}) => {
    const input = target.value;
    const v = input.slice(0,3);
    setYY(v);
    setExpYear(v? v : '00');
  }

  return (
    <div className='w-[55%] flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">Exp. date (MM/YY)</label>
      <div className='w-full flex justify-between gap-1'>
        <input value={mm} onChange={handleExpMonthInput} className='numberInput w-[50%] border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg' type="number" inputMode='numeric' placeholder='MM' />
        <input value={yy} onChange={handleExpYearInput} className='numberInput w-[50%] border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg' type="number" inputMode='numeric' placeholder='YY' />
      </div>
      
    </div>
  )
}
export default ExpDateInput;
