import React, { useState } from 'react';

function CvcInput({setCvc}) {
  const [cvcInput, setCvcInput] = useState('');

  const handleCvcInput = ({target}) => {
    const input = target.value;
    const numericCvc = input.replace(/\D/g, ''); // Remove any non-numeric characters
    const truncatedCvc = numericCvc.slice(0, 3);
    setCvc(truncatedCvc);
  }

  return (
    <div className='w-[40%] flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">cvc</label>
      <input value={cvcInput} onChange={handleCvcInput} className='numberInput border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg' type="number" inputMode='numeric' placeholder='e.g. 123' />
    </div>
  )
}
export default CvcInput;
