import React, { useState } from 'react';

// Components
import Error from './Error';

function CvcInput({setCvc}) {
  const [cvcInput, setCvcInput] = useState('');
  const [error, setError] = useState(false);

  const handleCvcInput = ({target}) => {
    const input = target.value;
    const truncatedCvc = input.slice(0, 3);
    setCvcInput(truncatedCvc);
    setCvc(truncatedCvc ? truncatedCvc : '000');
  }

  return (
    <div className='w-[40%] flex flex-col gap-2'>
      <label className="uppercase tracking-widest text-xs font-semibold" style={{color: 'hsl(278, 68%, 11%)'}} htmlFor="">cvc</label>
      <input value={cvcInput} onChange={handleCvcInput} className={`numberInput ${error ? 'border-red-500' : 'border-slate-200'} border-[.5px] border-solid focus:border-violet-800 p-2 outline-none rounded-lg `} type="number" inputMode='numeric' placeholder='e.g. 123' />
      {
        error && (
          <Error />
        )
      }
    </div>
  )
}
export default CvcInput;
